package com.example.look.blue;import android.annotation.SuppressLint;import android.bluetooth.BluetoothDevice;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.look.R;import java.util.List;public class BtDeviceAdapter extends RecyclerView.Adapter<BtDeviceAdapter.BtHolder> {    List<BtDeviceBean> mDeviceList;    BtItemClickListener mBtClickListener;    public BtDeviceAdapter(List<BtDeviceBean> deviceList) {        mDeviceList = deviceList;    }    @NonNull    @Override    public BtHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_bt_info, parent, false);        return new BtHolder(view);    }    @Override    public void onBindViewHolder(@NonNull BtHolder holder, @SuppressLint("RecyclerView") final int position) {        BtDeviceBean device = mDeviceList.get(position);        holder.tvBtName.setText(device.mBtName);        holder.tvBtAddress.setText(device.mBtAddress);        if (device.mBtStatus == BluetoothDevice.BOND_NONE) {            holder.tvBtStatus.setText("未配对");        }        if (device.mBtStatus == BluetoothDevice.BOND_BONDED) {            holder.tvBtStatus.setText("已配对");        }        if (device.mBtStatus == 14) {            holder.tvBtStatus.setText("已连接");        }        holder.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                if (mBtClickListener != null) {                    mBtClickListener.onBtItemClick(position);                }            }        });    }    @Override    public int getItemCount() {        return mDeviceList != null ? mDeviceList.size() : 0;    }    public void setOnItemClickListener(BtItemClickListener listener) {        this.mBtClickListener = listener;    }    public interface BtItemClickListener {        void onBtItemClick(int position);    }    public static class BtHolder extends RecyclerView.ViewHolder {        TextView tvBtName, tvBtAddress, tvBtStatus;        public BtHolder(@NonNull View itemView) {            super(itemView);            tvBtName = itemView.findViewById(R.id.tv_bt_name);            tvBtAddress = itemView.findViewById(R.id.tv_bt_address);            tvBtStatus = itemView.findViewById(R.id.tv_bt_status);        }    }}