package com.example.look.blue;import androidx.annotation.NonNull;import java.io.Serializable;public class BtDeviceBean implements Serializable {    public String mBtName;    public String mBtAddress;    /**     * desc: 10未配对 12已配对 14已连接     */    public int mBtStatus;    public BtDeviceBean(String mBtName, String mBtAddress, int mBtStatus) {        this.mBtName = mBtName;        this.mBtAddress = mBtAddress;        this.mBtStatus = mBtStatus;    }    @NonNull    @Override    public String toString() {        return "BtDeviceBean{" +                "mBtName='" + mBtName + '\'' +                ", mBtAddress='" + mBtAddress + '\'' +                ", mBtStatus=" + mBtStatus +                '}';    }}